mapper.select:
SELECT EMAIL, NICKNAME, AUTH
FROM USER
WHERE EMAIL = #{email}

SELECT EMAIL, PWD, NICKNAME, AUTH
FROM USER
WHERE PWD = #{pwd}

mapper.update:
UPDATE USER
SET PROFILE  = #{profile},
    AUTH     = #{auth},
    NICKNAME = #{nickname}
WHERE EMAIL = #{email}

UPDATE USER
SET PWD = #{pwd}
WHERE EMAIL = #{email}

mapper.select:
SELECT IF(COUNT(*) = 1, 1, 0) AS "EXISTS"
FROM USER
WHERE EMAIL = #{email}
